/**
 * Bindings map
 * @type {Map}
 */
const bindings = new Map();

/**
 * Simple DI implementation
 */
export default class DI {
    /**
     * Bind item by name
     * @param {String} name
     * @param {*} item
     */
    static bind( name, item ) {
        bindings.set( name, item );
    }

    /**
     * Get item from container by name
     * @param {String} name
     * @return {*}
     */
    static resolve( name ) {
        if( bindings.has( name ) ) {
            return bindings.get( name );
        }
    }
}

/**
 * Inject item by name
 * @param target
 * @param {String} key
 * @param descriptor
 */
export function inject( target, key, descriptor ) {
    const name = descriptor.initializer();
    descriptor.initializer = function() {
        return DI.resolve( name )
    };
}

/**
 * Decorator for mark property as default value of options
 * @param target
 * @param name
 * @param descriptor
 * @return {*}
 */
export default function( target, name, descriptor ) {
    target.defaultOptionProps = ( target.defaultOptionProps || [] ).concat( [ name ] );
    return descriptor;
}

/**
 * Работа с конечным автоматом
 * @module shamUI/fsm
 */

import { inject } from '../DI';
import { Fsm } from '../utils/fsm';
import ReadyState from "./states/ready";
import RegistrationState from "./states/registration";
import RenderingState from "./states/rendering";

/**
 * Default states
 * @type {
 *  {
 *    ready:         ReadyState,
 *    registration:  RegistrationState,
 *    rendering:     RenderingState
 *  }
 * }
 */
export const states = {
    ready:         ReadyState,
    registration:  RegistrationState,
    rendering:     RenderingState
};

/**
 * Класс основного конечного атомата
 */
export class FSM extends Fsm {
    @inject ready = 'state:ready';
    @inject registration = 'state:registration';
    @inject rendering = 'state:rendering';
    @inject logger = 'logger';

    constructor() {
        super( ...arguments );
        this.widgets = [];
        this.idArray = [];
        this.byType = {};
        this.byId = {};
    }

    static initialState = "ready";

    /**
     * Перерисовать все
     * @see {@link ReadyState#all}
     */
    ALL() {
        this.handle( "all" );
    }

    /**
     * Перерисовать только те, ID которых переданны в аргументах
     * @see {@link ReadyState#only}
     * @param {...String} args Список ID виджетов, которые нужно отрисовать
     */
    ONLY( ...args ) {
        this.handle( "only", args );
    }

    /**
     * Перерисовать все и переинициализировать
     * @see {@link ReadyState#forceAll}
     */
    FORCE_ALL() {
        this.handle( "forceAll" );
    }

    /**
     * Отрисовать указанные виджеты. Помимо перерисовки еще и польностье перерегистриует
     * указанные виджеты
     * @see {@link ReadyState#forceOnly}
     * @param {...String} args Список ID виджетов, которые нужно отрисовать
     */
    FORCE_ONLY( ...args ) {
        this.handle( "forceOnly", args );
    }

    /**
     * Перерисовать только с указанными типами
     * @param {...String} args Список типов виджетов, которые нужно отрисовать
     * @see {@link ReadyState#onlyType}
     */
    ONLY_TYPE( ...args ) {
        this.handle( "onlyType", args );
    }

    /**
     * Зарегистрировать виджет
     * @param {*}         widget.ID                     Идентификатор
     * @param {Function} [widget.bind]                   Функция навешивающая обработчики
     * @param {Function} [widget.render]                 Функция отрисовки.
     * @param {Function} [widget.destroy]                Функция отвязывающая обработчики
     * @param {Object}   [widget.options]                Опции виджета
     * @param {Array}    [widget.options.types]          Массив типов
     * @param {Function} [widget.options.beforeRegister] До регистрации
     * @param {Function} [widget.options.afterRegister]  После регистрации
     * @param {Function} [widget.options.beforeBind]     До навешивания обработчиков
     * @param {Function} [widget.options.afterBind]      После навешивания обработчиков
     * @param {Function} [widget.options.beforeRender]   До отрисовки этого элемента
     * @param {Function} [widget.options.afterRender]    После отрисовки этого элемента
     * @param {Function} [widget.options.beforeDestroy]  До отвязки обработчиков
     * @param {Function} [widget.options.afterDestroy]   После отвязки обработчиков
     * @see {@link RegistrationState#register}
     */
    register( widget ) {
        this.handle( "register", widget );
    }

    /**
     * Разрегистрировать виджет
     * @param {String} widgetId Идентификатор виджета
     */
    unregister( widgetId ) {
        this.handle( "unregister", widgetId );
    }

    /**
     * Hook for handle exception
     * @param {Object} exception
     * @private
     */
    handleException( exception ) {
        this.logger.error( exception );
        super.handleException( ...arguments );
    }
}

import { State } from '../../utils/fsm';

/**
 * Класс для состояния "Готов к работе"
 */
export default class ReadyState extends State {
    /**
     * Вызывается при входе в состояние
     */
    _onEnter() {
        this.emit( "Ready" );
    }

    /**
     * Удалить все виджеты
     */
    clear() {
        for ( let i = 0; i < this._fsm.widgets.length; i++ ) {
            if ( this._fsm.widgets[ i ].destroy ) {
                if ( this._fsm.widgets[ i ].options.beforeDestroy ) {
                    this._fsm.widgets[ i ].options.beforeDestroy.call( this._fsm.widgets[ i ] );
                }
                this._fsm.widgets[ i ].destroy();
                if ( this._fsm.widgets[ i ].options.afterDestroy ) {
                    this._fsm.widgets[ i ].options.afterDestroy.call( this._fsm.widgets[ i ] );
                }
            }
            this._fsm.widgets[ i ].isBinded = false;
        }
        this._fsm.widgets = [];
        this._fsm.idArray = [];
        this._fsm.byType = {};
        this._fsm.byId = {};
        this._fsm.renderCache = {};
    }

    /**
     * Отрисовать все виджеты. Просто отрисовывает, не вызывает destroy
     */
    all() {
        this._fsm.changeWidgets = this._fsm.idArray.slice( 0 );
        this.transition( "rendering" );
    }

    /**
     * Отрисовать только указанные виджеты. Просто отрисовывает, не вызывает destroy
     * @param {Array} needRenderingWidgets Список виджетов, которые нужно отрисовать
     */
    only( needRenderingWidgets ) {
        this._fsm.changeWidgets = needRenderingWidgets.slice( 0 );
        this.transition( "rendering" );
    }

    /**
     * Отрисовать все виджеты. Вызывает destroy, очищает список известных виджетов,
     * переходит к регистрации
     */
    forceAll() {
        this.handle( "clear" );
        this.transition( "registration" );
    }

    /**
     * Отрисовать указанные виджеты. Помимо перерисовки еще и польностье перерегистриует
     * указанные виджеты
     * @param {Array} needRenderingWidgets Список виджетов, которые нужно
     *                                     перерегистривароть и отрисовать
     */
    forceOnly( needRenderingWidgets ) {
        let index,
            widget,
            i;
        for ( i = 0; i < this._fsm.widgets.length; i++ ) {
            widget = this._fsm.widgets[ i ];
            index = needRenderingWidgets.indexOf( widget );
            if ( index > -1 ) {
                if ( widget.destroy  ) {
                    if ( widget.options.beforeDestroy ) {
                        widget.options.beforeDestroy.call( widget );
                    }
                    widget.destroy();
                    if ( widget.options.afterDestroy ) {
                        widget.options.afterDestroy.call( widget );
                    }
                }
                if ( widget.bindEvents ) {
                    if ( widget.options.beforeBindEvents ) {
                        widget.options.beforeBindEvents.call( widget );
                    }
                    widget.bind();
                    if ( widget.options.afterBindEvents ) {
                        widget.options.afterBindEvents.call( widget );
                    }
                }
                widget.isBinded = false;
            }
        }
        this._fsm.changeWidgets = needRenderingWidgets.slice( 0 );
        this.transition( "rendering" );
    }

    /**
     * Отрисовать виджеты с указанным типом
     * @param {Array} needRenderingWidgetsWithType Список типов, которые нужно отрисовать
     */
    onlyType( needRenderingWidgetsWithType ) {
        let argsTypes = needRenderingWidgetsWithType.slice( 0 ),
            widgetID,
            widgetsWithType,
            i;
        for ( i = 0; i < argsTypes.length; i++ ) {
            widgetsWithType = this._fsm.byType[ argsTypes[ i ] ];
            if ( widgetsWithType ) {
                for ( let j = 0, l = widgetsWithType.length; j < l; j++ ) {
                    widgetID = widgetsWithType[ j ];
                    if ( this._fsm.changeWidgets.indexOf( widgetID ) === -1 ) {
                        this._fsm.changeWidgets.push( widgetID );
                    }
                }
            }
        }
        this.transition( "rendering" );
    }

    /**
     * Зарегистрировать виджет (не из файла биндинга и конструкторов виджетов)
     * @see {@link RegistrationState#register}
     * @see Widget
     * @see Widget#defaultOptions
     */
    register( widget ) {

        // Регистрируем виджет
        this._fsm._states.registration.register( widget );

        // Если сначала биндим обработчики событий, а потом отрисовываем виджет
        if ( "bindEvents" === widget.options.actionSequence[ 0 ] &&
            widget.bindEvents ) {
            if ( widget.options.beforeBindEvents ) {
                widget.options.beforeBindEvents.call( widget );
            }
            widget.bindEvents();
            if ( widget.options.afterBindEvents ) {
                widget.options.afterBindEvents.call( widget );
            }
        }
    }

    /**
     * Разрегистрировать виджет
     * @param {String} widgetId Идентификатор виджета, который нужно разрегистрировать
     */
    unregister( widgetId ) {
        let widget,
            types,
            index,
            i;

        if ( this._fsm.idArray.indexOf( widgetId ) === -1 ) {
            return;
        }

        widget = this._fsm.byId[ widgetId ];

        if ( widget.destroy  ) {
            if ( widget.options.beforeDestroy ) {
                widget.options.beforeDestroy.call( widget );
            }
            widget.destroy();
            if ( widget.options.afterDestroy ) {
                widget.options.afterDestroy.call( widget );
            }
        }

        // Если есть типы, то удаляем ссылки на этот виджет
        if ( widget.options.types ) {
            types = widget.options.types;
            for ( i = 0; i < types.length; i++ ) {
                this._fsm.byType[ types[ i ] ].splice(
                    this._fsm.byType[ types[ i ] ].indexOf( widgetId ),
                    1
                )
            }
        }

        // Удаляем запись из массива виджетов
        for ( i = 0; i < this._fsm.widgets.length; i++ ) {
            if ( this._fsm.widgets[ i ].ID === widgetId ) {
                index = i;
                break;
            }
        }
        this._fsm.widgets.splice( index, 1 );

        // Удаляем из массива ID
        this._fsm.idArray.splice( this._fsm.idArray.indexOf( widgetId ), 1 );

        // И из объекта доступа по id
        delete this._fsm.byId[ widgetId ];

        delete this._fsm.renderCache[ widgetId ];
    }
}

import { inject } from '../../DI';
import { State } from '../../utils/fsm';

/**
 * Класс для состояния "Регистрация виджетов"
 */
export default class RegistrationState extends State {
    @inject widgetBinder = 'widget-binder';

    /**
     * Что делать с необрабатываемыми в этом состояния хэндлерами
     */
    _anyEvents() {
        this.deferUntilTransition();
    }

    /**
     * Вызывается при входе в состояние
     */
    _onEnter() {
        this.widgetBinder();
        this.handle( "registrationComplete" )
    }

    /**
     * Зарегистрировать виджет
     * @param {*}         widget.ID                      Идентификатор
     * @param {Function} [widget.bind]                   Функция навешивающая обработчики
     * @param {Function} [widget.render]                 Функция отрисовки.
     * @param {Function} [widget.destroy]                Функция отвязывающая обработчики
     * @param {Object}   [widget.options]                Опции виджета
     * @param {Array}    [widget.options.types]          Массив типов
     * @param {Function} [widget.options.beforeRegister] До регистрации
     * @param {Function} [widget.options.afterRegister]  После регистрации
     * @param {Function} [widget.options.beforeBind]     До навешивания обработчиков
     * @param {Function} [widget.options.afterBind]      После навешивания обработчиков
     * @param {Function} [widget.options.beforeRender]   До отрисовки этого элемента
     * @param {Function} [widget.options.afterRender]    После отрисовки этого элемента
     * @param {Function} [widget.options.beforeDestroy]  До отвязки обработчиков
     * @param {Function} [widget.options.afterDestroy]   После отвязки обработчиков
     * @see Widget
     * @see Widget#defaultOptions
     */
    register( widget ) {

        // Добавляем только те, которых еще нет
        if ( this._fsm.idArray.indexOf( widget.ID ) === -1 ) {
            if ( widget.options.beforeRegister ) {
                widget.options.beforeRegister.call( widget );
            }
            this._fsm.idArray.push( widget.ID );
            this._fsm.byId[ widget.ID ] = widget;

            // Если есть типы
            if ( widget.options.types ) {
                for ( let i = 0; i < widget.options.types.length; i++ ) {
                    if ( this.byType[ widget.options.types[ i ] ] === undefined ) {
                        this.byType[ widget.options.types[ i ] ] = [];
                    }
                    this.byType[ widget.options.types[ i ] ].push( widget.ID );
                }
            }

            this._fsm.widgets.push( widget );
            if ( widget.options.afterRegister ) {
                widget.options.afterRegister.call( widget );
            }
        }
    }

    /**
     * Все виджеты зарегисрированы
     * Если нужно, то сначала биндим обработчики событий, а потом отрисовываем
     */
    registrationComplete() {
        let widget;
        this.emit( "RegistrationComplete" );
        for ( let i = 0; i < this._fsm.widgets.length; i++ ) {
            widget = this._fsm.widgets[ i ];

            // Если сначала биндим обработчики событий, а потом отрисовываем виджет
            if ( "bindEvents" === widget.options.actionSequence[ 0 ] &&
                widget.bindEvents ) {
                if ( widget.options.beforeBindEvents ) {
                    widget.options.beforeBindEvents.call( widget );
                }
                widget.bindEvents();
                if ( widget.options.afterBindEvents ) {
                    widget.options.afterBindEvents.call( widget );
                }
            }
        }
        this._fsm.changeWidgets = this._fsm.idArray.slice( 0 );
        this.transition( "rendering" );
    }
}

import { State } from '../../utils/fsm';

/**
 * @classdesc Deferred на нативных Promise
 * todo: move to utils?
 * @returns {*}
 * @constructor
 * @class Deferred
 */
function Deferred() {
    this.promise = new Promise( function( resolve, reject ) {
        this.resolve = resolve;
        this.reject = reject;
    }.bind( this ) );
    return this;
}

/**
 * Класс для состояния "Отрисовываем виджеты"
 */
export default class RenderingState extends State {
    /**
     * Что делать с необрабатываемыми в этом состояния хэндлеры
     */
    _anyEvents() {
        this.deferUntilTransition();
    }

    /**
     * Вызывается при входе в это состояние
     */
    _onEnter() {
        let self = this,
            promises = [],
            promiseById = {},
            widgetDependencePromise;

        this.cmpWidget = function( a, b ) {
            const indexA = self._fsm.idArray.indexOf( a ),
                indexB = self._fsm.idArray.indexOf( b );
            if ( indexA > indexB ) {
                return 1;
            } else if ( indexA < indexB ) {
                return -1;
            } else {
                return 0;
            }
        };

        // Сортируем по renderDependence
        this._fsm.changeWidgets.sort( function( a, b ) {
            const widgetARD = self._fsm.byId[ a ].options.renderDependence,
                widgetBRD = self._fsm.byId[ b ].options.renderDependence;
            if ( !widgetARD || !widgetARD.length ) {
                if ( !widgetBRD || !widgetBRD.length ) {

                    // У обоих виджетов нет зависимостей
                    return self.cmpWidget( a, b );
                } else {

                    // Зависит ли виджет B от виджета A
                    return widgetBRD.indexOf( a ) !== -1 ? -1 : self.cmpWidget( a, b );
                }
            } else {
                if ( !widgetBRD || !widgetBRD.length ) {

                    // Зависит ли виджет A от виджета B
                    return widgetARD.indexOf( b ) !== -1 ? 1 : self.cmpWidget( a, b );
                } else {

                    // У обоих виджетов есть зависимости
                    if ( widgetARD.indexOf( b ) !== -1 ) {

                        // Виджет A зависит от виджета B
                        return 1;
                    } else if ( widgetBRD.indexOf( a ) !== -1 ) {

                        // Виджет B зависит от виджета A
                        return -1;
                    } else {

                        // Виджеты не зависят друг от друга
                        return self.cmpWidget( a, b );
                    }
                }
            }
        } );

        this.rendered = [];

        for ( let i = 0; i < this._fsm.changeWidgets.length; i++ ) {
            let current = this._fsm.byId[ this._fsm.changeWidgets[ i ] ];
            if ( current && current.render ) {
                if ( !current.options.conditionRender ||
                    current.options.conditionRender() ) {
                    const deferred = new Deferred();
                    promises.push( deferred.promise );
                    promiseById[ this._fsm.changeWidgets[ i ] ] = deferred.promise;

                    if ( current.options.renderDependence &&
                        current.options.renderDependence.length ) {
                        widgetDependencePromise = new Array(
                            current.options.renderDependence.length
                        );
                        for ( let j = 0; j < current.options.renderDependence.length; j++ ) {
                            widgetDependencePromise[ j ] = promiseById[
                                current.options.renderDependence[ j ]
                                ];
                        }
                    } else {
                        widgetDependencePromise = [];
                    }

                    ( function( widget, widgetDependencePromise, deferred ) {

                        // Отрисовываем этот виджет только после того, как
                        // все зависимости отрисовались
                        Promise.all( widgetDependencePromise )
                            .then( function() {
                                if ( widget.options.renderAsync ) {
                                    widget.options.renderAsyncWrapper(
                                        function() {
                                            self.handle( "renderWidget", widget, deferred );
                                        }
                                    );
                                } else {
                                    self.handle( "renderWidget", widget, deferred );
                                }
                            }, self.handleException.bind( self ) );
                    } )( current, widgetDependencePromise, deferred );
                }
            }
        }

        Promise.all( promises ).then( function() {
            // Все области отрисовались
            promiseById = null;

            // Так же обрабатываем по отдельности
            for ( let i = 0; i < self.rendered.length; i++ ) {
                self.emit( [
                               "RenderComplete[", self.rendered[ i ], "]"
                           ].join( "" ) );
            }

            // И все сразу
            self.emit( "RenderComplete", self.rendered );
            self.transition( "ready" );
        }, this.handleException.bind( this ) );
    }

    /**
     * Вызывается при выходе из этого состояни
     */
    _onExit() {
        this.rendered = [];
        this._fsm.changeWidgets = [];
    }

    /**
     * Отрисовать один виджет
     * @param {Object} widget   Виджет
     * @param {Object} deferred Отложенный объект для этого виджета
     * @see Widget
     */
    renderWidget( widget, deferred ) {
        if ( widget.options.beforeRender ) {
            widget.options.beforeRender.call( widget );
        }
        this.rendered.push( widget.ID );

        new Promise( ( resolve ) => {
            const obj = widget.render();
            if ( !obj ) {
                resolve();
                return;
            }
            if ( obj.html ) {
                const newEl = obj.container.cloneNode( false );
                newEl.innerHTML = obj.html;
                obj.container.parentNode.replaceChild( newEl, obj.container );
                widget.container = newEl;
                resolve();
            }

        } ).then( () => {
            if ( widget.options.afterRender ) {
                widget.options.afterRender.call( widget );
            }
            if ( widget.options.actionSequence[ 0 ] === "render" &&
                widget.bindEvents && !widget.isBinded ) {

                // Если после отрисовки нужно биндить обработчики событий виджета или нет
                if ( widget.options.beforeBindEvents ) {
                    widget.options.beforeBindEvents.call( widget );
                }
                widget.bindEvents();
                if ( widget.options.afterBindEvents ) {
                    widget.options.afterBindEvents.call( widget );
                }
                if ( widget.options.bindOnce ) {
                    widget.isBinded = true;
                }
            }
            deferred.resolve();
        } ).catch( this.handleException.bind( this ) );
    }
}

import { default as DIContainer, inject as DIInject } from './DI';
import { FSM, states } from './fsm/fsm';
import BaseWidget from './widget';
import decoratorOptions from './decorators/options';
import { State } from './utils/fsm';

export const DI = DIContainer;
export const inject = DIInject;

/** @link {Widget} */
export const Widget = BaseWidget;

export const Fsm = FSM;
export const FsmStates = states;
export const FsmState = State;

export const options = decoratorOptions;

// Default fsm binding
DI.bind( 'fsm', FSM );

// Default state binding
DI.bind( 'state:ready', states.ready );
DI.bind( 'state:registration', states.registration );
DI.bind( 'state:rendering', states.rendering );

// Default logger
DI.bind( 'logger', console );

/**
 * Фабрика для создания экземляров библиотеки
 *
 * @property {Object} render Доступ к конечному автомату {@link module:shamUI/fsm}
 */
export default class ShamUI {
    /**
     * Создать экземпляр
     */
    constructor() {
        DI.bind( 'sham-ui', this );
        const Fsm = DI.resolve( 'fsm' );
        this.render = new Fsm();
        this.render.run();
    }
}

const NEXT_TRANSITION = "transition";
const NEXT_HANDLER = "handler";
const HANDLING = "handling";
const HANDLED = "handled";
const NO_HANDLER = "nohandler";
const TRANSITION = "transition";
const INVALID_STATE = "invalidstate";
const DEFERRED = "deferred";
const ERROR = "error";


/**
 * Finite State Machine
 */
export class Fsm {
    constructor() {
        this.targetReplayState = "";
        this.state = undefined;
        this.priorState = undefined;
        this._priorAction = "";
        this._currentAction = "";
        this.eventListeners = {
            _anyEvents: []
        };
        this.eventQueue = [];
    }

    /**
     * Initial states
     * @type {string}
     */
    static initialState = "uninitialized";

    /**
     * Initialize states
     * Default resolve from injected
     */
    initStates() {
        this._states = {};
        for ( let key in this ) {
            if ( this[ key ] && this[ key ].prototype instanceof State ) {
                this._states[ key ] = new this[ key ]( this );
            }
        }
    };

    /**
     * Transition FSM to initialState
     */
    run() {
        this.initStates();
        this.transition( this.constructor.initialState );
    }

    /**
     * Fire event
     * @param {string} eventName
     */
    emit( eventName ) {
        if ( this.eventListeners._anyEvents ) {
            this.eventListeners._anyEvents.forEach( callback => {
                callback( ...arguments );
            } );
        }

        if ( this.eventListeners[ eventName ] ) {
            this.eventListeners[ eventName ].forEach( callback => {
                callback( ...arguments );
            } );
        }
    }

    /**
     * Call handler in state
     * @param {string} inputType
     */
    handle( inputType ) {
        if ( !this.inExitHandler ) {
            const states = this._states,
                current = this.state;

            const args = Array.prototype.slice.call( arguments, 0 );
            this.currentActionArgs = args;

            if ( states[ current ][ inputType ] ||
                states[ current ]._anyEvents ||
                this._anyEvents ) {
                const handlerName = states[ current ][ inputType ] ? inputType : "_anyEvents";
                const catchAll = "_anyEvents" === handlerName;

                let handler, action;
                if ( states[ current ][ handlerName ] ) {
                    handler = states[ current ][ handlerName ];
                    action = `${current}.${handlerName}`;
                } else {
                    handler = this._anyEvents;
                    action = "_anyEvents";
                }
                if ( !this._currentAction ) {
                    this._currentAction = action;
                }

                this.emit( HANDLING, {
                    inputType,
                    args
                } );
                handler.apply( states[ current ], catchAll ? args : args.slice( 1 ) );
                this.emit( HANDLED, {
                    inputType,
                    args
                } );

                this._priorAction = this._currentAction;
                this._currentAction = "";
                this.processQueue( NEXT_HANDLER );
            } else {
                this.emit( NO_HANDLER, {
                    inputType,
                    args
                } );
            }

            this.currentActionArgs = undefined;
        }
    }

    /**
     * Transition to state
     * @param {String} newState
     */
    transition( newState ) {
        if ( !this.inExitHandler && newState !== this.state ) {
            const curState = this.state;
            if ( this._states[ newState ] ) {
                if ( curState && this._states[ curState ] && this._states[ curState ]._onExit ) {
                    this.inExitHandler = true;
                    this._states[ curState ]._onExit();
                    this.inExitHandler = false;
                }
                this.targetReplayState = newState;
                this.priorState = curState;
                this.state = newState;
                this.emit( TRANSITION, {
                    fromState: this.priorState,
                    action: this._currentAction,
                    toState: newState
                } );
                if ( this._states[ newState ]._onEnter ) {
                    this._states[ newState ]._onEnter();
                }
                if ( this.targetReplayState === newState ) {
                    this.processQueue( NEXT_TRANSITION );
                }
                return;
            }
            this.emit( INVALID_STATE, {
                state: this.state,
                attemptedState: newState
            } );
        }
    }

    /**
     * Process events queue by type
     * @param {String} type
     */
    processQueue( type ) {
        const filterFn = NEXT_TRANSITION === type ?
            item => NEXT_TRANSITION === item.type && (
                (
                    !item.untilState
                ) || (
                item.untilState === this.state
                )
            ) : item => NEXT_HANDLER === item.type;

        let toProcess = [];

        let counter = -1,
            length = this.eventQueue.length,
            index = 0,
            item;
        while ( ++counter < length ) {
            item = this.eventQueue[ counter - index ];
            if ( filterFn( item ) ) {
                toProcess.push( this.eventQueue.splice( counter - index++ )[ 0 ] );
            }
        }

        toProcess.forEach( item => {
            this.handle( ...item.args );
        } );
    }

    /**
     * Defer current action from current state to destination state
     * @param {string} stateName Destination state
     */
    deferUntilTransition( stateName ) {
        if ( this.currentActionArgs ) {
            const queued = {
                type: NEXT_TRANSITION,
                untilState: stateName,
                args: this.currentActionArgs
            };
            this.eventQueue.push( queued );
            this.emit.call( this, DEFERRED, {
                state: this.state,
                queuedArgs: queued
            } );
        }
    }

    /**
     * Subscribe listener to event
     * @param {String}   eventName
     * @param {Function} callback
     * @return {{eventName: string, callback: Function, off: Function}}
     */
    on( eventName, callback ) {
        if ( !this.eventListeners[ eventName ] ) {
            this.eventListeners[ eventName ] = [];
        }
        this.eventListeners[ eventName ].push( callback );
        return {
            eventName,
            callback,
            off: () => {
                this.off( eventName, callback );
            }
        };
    }

    /**
     * Un-subscribe listener
     * @param {=string}   eventName
     * @param {=Function} callback
     */
    off( eventName, callback ) {
        if ( !eventName ) {
            this.eventListeners = {};
        } else {
            if ( this.eventListeners[ eventName ] ) {
                if ( callback ) {
                    let index = 0,
                        counter = -1,
                        length = this.eventListeners[ eventName ].length,
                        item;
                    while ( ++counter < length ) {
                        item = this.eventListeners[ eventName ][ counter - index ];
                        if ( callback === item ) {
                            this.eventListeners[ eventName ].splice( counter - index++ );
                        }
                    }
                } else {
                    this.eventListeners[ eventName ] = [];
                }
            }
        }
    }

    /**
     * Subscribe once event listener
     * @param {String}   eventName
     * @param {Function} callback
     */
    one( eventName, callback ) {
        const off = this.on( eventName, () => {
            callback( ...arguments );
            off();
        } ).off;
    }

    /**
     * Hook for process error
     * @param {Object} exception
     */
    handleException( exception ) {
        this.emit( ERROR, {
            exception,
            state: this.state,
            priorState: this.priorState,
            _currentAction: this._currentAction,
            _priorAction: this._priorAction,
            currentActionArgs: this.currentActionArgs,
        } );
    }
}

export class State {
    constructor( fsmInstance ) {
        this._fsm = fsmInstance;
    }

    /**
     * Call handler in state
     * @param {String} inputType
     */
    handle( inputType ) {
        this._fsm.handle( ...arguments );
    }

    /**
     * Transition to state
     * @param {String} newState
     */
    transition( newState ) {
        this._fsm.transition( ...arguments );
    }

    deferUntilTransition() {
        this._fsm.deferUntilTransition( ...arguments );
    }

    emit() {
        this._fsm.emit( ...arguments );
    }

    /**
     * Hook for process errors
     */
    handleException() {
        this._fsm.handleException( ...arguments )
    }
}

import options from './decorators/options';
import { inject } from './DI';

/**
 * Базовый класс для виджетов
 */
export default class Widget {
    @inject UI = 'sham-ui'; // inject shamUI instance as this.UI

    /**
     * @param {String}  containerSelector CSS-селектор элемента, в который будет
     *                                    происходить отрисовка
     * @param {String}  ID                Уникальный идентификтор
     * @param {Object} [options]          Опции
     */
    constructor( containerSelector, ID, options ) {

        this.ID = ID;
        /**
         * @type {null|Node} Container of this widget
         */
        this.container = null;
        this.containerSelector = containerSelector;
        this.options = options;
        this.UI.render.register( this )
    }

    /**
     * Сначала биндим обработчики событий, потом отрисовываем
     * @type {Array}
     * @default [ "bindEvents", "render" ]
     */
    @options
    actionSequence = [ "bindEvents", "render" ];

    /**
     * Тип виджета
     * @type {Array}
     */
    @options
    types = [];

    /**
     * Если биндим обработчики событий после отрисовки, то нужно ли перебиндиивать их
     * после каждой отрисовки
     * @member {Boolean}
     * @default true
     */
    @options
    bindOnce = true;

    /**
     * Нужно ли кэшировать родительский элемент для контейнера
     * @type {Boolean}
     * @default false
     */
    @options
    cacheParentContainer = false;

    /**
     * Виджет отрисовывается ассинхронно
     * @type {Boolean}
     * @default false,
     */
    @options
    renderAsync = false;

    /**
     * Обертка для ассинхроной отрисовки
     * @deprecated
     * @param {Function} renderCallback callback для рендера
     */
    @options
    static renderAsyncWrapper( renderCallback ) {
        window.requestAnimationFrame( renderCallback );
    }

    /**
     * Массив виджетов, которые нужно отрисовать перед тем, как отрисовывать этот виджет
     * @type {Array}
     */
    @options
    renderDependence = [];

    /**
     * Опции виджета. Переопределяют опции по-умолчанию
     * @see #Widget
     * @return {Object}
     */
    get options() {
        return this._options;
    }

    /**
     * @param {Object} options
     */
    set options( options ) {

        // Выставляем опции по-умолчанию
        this._options = {};
        const _options = options || {};
        for ( let name in _options ) {
            if ( !_options.hasOwnProperty( name ) ) {
                continue;
            }
            this._options[ name ] = _options[ name ];
        }

        const optionsOwners = [
            this.constructor, // Static options
            this              // Instance options
        ];
        for ( let owner of optionsOwners ) {
            const defaultStaticOptionProps = owner[ 'defaultOptionProps' ] || [];
            for ( let name of defaultStaticOptionProps ) {
                if ( !this._options.hasOwnProperty( name ) ) {
                    this._options[ name ] = owner[ name ];
                }
            }
        }
    }

    /**
     * Функция возвращающая html для отрисовки
     */
    html() {};

    /**
     * Добавить обработчики событий
     */
    bindEvents() {};

    /**
     * Функция вызывающая при уничтожениии виджета
     */
    destroy() {};

    /**
     * Отрисовать виджет в контейнер
     * @returns {{container: *, html: *}}
     */
    render() {
        if ( this.options.cacheParentContainer ) {
            if ( !this.containerParentNode ) {
                this.containerParentNode = document.querySelector(
                    this.containerSelector
                ).parentNode;
            }
            this.container = this.containerParentNode.querySelector(
                this.containerSelector
            );
        } else {
            this.container = document.querySelector( this.containerSelector );
        }
        return {
            container: this.container,
            html: this.html()
        }
    }
}
